    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(login.getText().equals("admin") && Integer.parseInt(senha.getText()) == senhaEsperada){
            int opcao = -1;
            JOptionPane.showMessageDialog(null, "Login efetuado com sucesso!!!");
            TelaMenu menu = new TelaMenu();
            try {
                menu.atualizarclientes();
                menu.atualizarfuncionarios();
                menu.atualizarequipamentos();
            } catch (IOException ex) {
                Logger.getLogger(TelaLogin.class.getName()).log(Level.SEVERE, null, ex);
            }
            menu.setVisible(true);
            this.dispose();
        }
        else{
           JOptionPane.showMessageDialog(null, "         Nome de usu√°rio ou senha incorreta!");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

